function y = f(x)    global G path    p = path(floor(x(1)),:); % 选择路径    t = 0; % 已用时间    t_w = sum(x(2:end)) + 30 + 30; % 游览时间    for i = 1:length(p)-1  % 计算行走时间       t = t + G(p(i),p(i+1)); % 行走时间增加        if p(i+1) == 4 %如果到了深林小剧场           if mod(t,30) > 1e-4               t = t + (30-mod(t,30));           end           t = t + 30;       elseif p(i+1) < 4           t = t + x(p(i+1));       elseif p(i+1) < 8           t = t + x(p(i+1)-1);       end       if p(i+1) == 2 % 如果即将到达游客服务中心，考虑其16:00关门           if t > 240               t_w = t_w - (t-240 + 1) * 1000; % 游览时间剧减,迟到游客服务中心的惩罚           end       end    end    if t > 300 % 17:00到不了湿地商业街，游览时间剧减        t_w = t_w - (t - 300 + 1) * 1000;    else        t_w = t_w + (300 - t);    end    y = - t_w;end